mapper.select:

SELECT  NVL(MEM_ID, 0) id
FROM    MEMBER
WHERE   MEM_NAME = #{MEM_NAME}
AND     MEM_EMAIL = #{MEM_EMAIL}
AND     MEM_DEL_GB = 'N'


SELECT  MEM_PW

FROM    MEMBER
WHERE   MEM_ID = #{MEM_ID}
AND     MEM_EMAIL = #{MEM_EMAIL}
AND     MEM_DEL_GB = 'N'

mapper.update:

    UPDATE  MEMBER
	SET     MEM_PW = #{password}
	WHERE   MEM_ID = #{MEM_ID}
	AND     MEM_EMAIL = #{MEM_EMAIL}
	AND     MEM_DEL_GB = 'N'

mapper.select:

SELECT ORDER_NUM
FROM MK_ORDER
WHERE ORDER_NAME=#{ORDER_NAME} AND ORDER_PHONE=#{ORDER_PHONE}
